import { Component } from '@angular/core';
import { FormBuilder, FormControl, NgForm } from '@angular/forms';
import { FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { LoginService } from 'src/app/services/login.service';
import { UserService } from 'src/app/services/user.service';
import { user } from './user';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  constructor(private route:Router, private userService : UserService,private loginService: LoginService){}


  loginForm = new FormGroup({
    email: new FormControl(''),
    password: new FormControl('')
  })

  private userObj:user = new user();



 login(){
  alert("logged in");
  this.userObj.username.setValue(this.loginForm.email.value))
  console.log(this.loginForm.value);

  this.loginService(this.loginForm)
 }


}




    <div class="card mx-auto col-8 col-md-8 col-lg-6" style="background-color: black; width: 20%;margin-top: 10%;" >

        <label class="text-center" style="color: aliceblue; margin-bottom: 5px;margin-top: 5px; font-size:x-large;">Login Here..</label>
    <div class="card-body">

        <form [formGroup]="loginForm" (ngSubmit)="login()">
        <div class="form-floating mb-3 ">
            <input type="email" class="form-control" formControlName="email" placeholder="name@example.com">
            <label for="floatingInput">Email address</label>
        </div>
        <div class="form-floating">
            <input type="password" class="form-control" formControlName="password" placeholder="Password">
            <label for="floatingPassword">Password</label>
        </div>
        <div  class="text-center" style="margin-top: 8%;">
            <button  type="submit"  class="btn btn-primary" style="margin-left: 10%;">Login</button>
            <button  type="submit" class="btn btn-info" style="margin-left: 8%; margin-right: 10%;">Sign-Up</button> 
        </div>
    </form>
    </div>
</div>
