import { Component } from '@angular/core';
import { FormGroup } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  loginForm : FormGroup;

  constructor(private fb:FOrmB)

  login(){
    alert("signed in");
  }
}

    <div class="card mx-auto col-8 col-md-8 col-lg-6" style="background-color: black; width: 20%;margin-top: 10%;" >

        <label class="text-center" style="color: aliceblue; margin-bottom: 5px;margin-top: 5px; font-size:x-large;">Login Here..</label>
    <div class="card-body">

        <form [formGroup]="loginForm" (ngSubmit)="login()">
        <div class="form-floating mb-3 ">
            <input type="email" class="form-control" id="email" placeholder="name@example.com">
            <label for="floatingInput">Email address</label>
        </div>
        <div class="form-floating">
            <input type="password" class="form-control" id="Password" placeholder="Password">
            <label for="floatingPassword">Password</label>
        </div>
        <div  class="text-center" style="margin-top: 8%;">
            <button  type="submit" onclick="login()" class="btn btn-primary" style="margin-left: 10%;">Login</button>
            <button  type="submit" class="btn btn-info" style="margin-left: 8%; margin-right: 10%;">Sign-Up</button> 
        </div>
    </form>
    </div>
</div>
